'use strict';

// VARIABLES //

var invalid;
var test;


// MAIN //

// Create our test cases:
invalid = [];

test = {
	'code': [
		'/**',
		'* Beep boop.',
		'*',
		'* 1.  Item',
		'* 2.   Item',
		'*/',
		'function beep() {',
		'    console.log( "boop" );',
		'}'
	].join( '\n' ),
	'errors': [
		{
			'message': 'Incorrect list-item indent: remove 1 space',
			'type': null
		}
	]
};
invalid.push( test );

test = {
	'code': [
		'/**',
		'* Beep boop.',
		'*',
		'* 1.  Item',
		'* 2.    Item',
		'*/',
		'function beep() {',
		'    console.log( "boop" );',
		'}'
	].join( '\n' ),
	'errors': [
		{
			'message': 'Incorrect list-item indent: remove 2 spaces',
			'type': null
		}
	]
};
invalid.push( test );

test = {
	'code': [
		'/**',
		'* Beep boop.',
		'*',
		'* 1.  Item',
		'* 2.  Item',
		'*/',
		'function beep() {',
		'    console.log( "boop" );',
		'}'
	].join( '\n' ),
	'options': [ 'space' ],
	'errors': [
		{
			'message': 'Incorrect list-item indent: remove 1 space',
			'type': null
		},
		{
			'message': 'Incorrect list-item indent: remove 1 space',
			'type': null
		}
	]
};
invalid.push( test );

test = {
	'code': [
		'/**',
		'* Beep boop.',
		'*',
		'* 1.  Item',
		'*     Wrapped',
		'*',
		'* 2.  Item',
		'*     Wrapped',
		'*',
		'* ## Bar',
		'*',
		'* 1.  Item',
		'* 2. Item',
		'*/',
		'function beep() {',
		'    console.log( "boop" );',
		'}'
	].join( '\n' ),
	'options': [ 'mixed' ],
	'errors': [
		{
			'message': 'Incorrect list-item indent: remove 1 space',
			'type': null
		}
	]
};
invalid.push( test );


// EXPORTS //

module.exports = invalid;
