'use strict';

// VARIABLES //

var invalid;
var test;


// MAIN //

// Create our test cases:
invalid = [];

test = {
	'code': [
		'/**',
		'* Beep boop.',
		'*',
		'* ```bash',
		'* $ echo a',
		'* $ echo a > file',
		'* ```',
		'*',
		'* @param {string} str - input value',
		'* @returns {string} output value',
		'*',
		'* @example',
		'* var out = beep( "boop" );',
		'* // returns "beepboop"',
		'*/',
		'function beep( str ) {',
		'    return "beep" + str;',
		'}'
	].join( '\n' ),
	'errors': [
		{
			'message': 'Do not use dollar signs before shell commands',
			'type': null
		}
	]
};
invalid.push( test );


// EXPORTS //

module.exports = invalid;
