tape( 'the command-line interface prints a license header regular expression', opts, function test( t ) {
	var expected;
	var fixture;
	var fopts;
	var file;
	var cmd;

	cmd = [
		EXEC_PATH,
		fpath,
		'--license=Apache-2.0',
		'--type=js'
	];

	fopts = {
		'encoding': 'utf8'
	};

	file = resolve( __dirname, 'fixtures', 're_apache-2.0.js.txt' );
	expected = readFileSync( file, fopts );
	expected = expected.split( '\n' );
	expected = expected.slice( 0, expected.length-1 ).join( '\\n' ) + '\n';

	file = resolve( __dirname, 'fixtures', 'file.js.txt' );
	fixture = readFileSync( file, fopts );

	exec( cmd.join( ' ' ), done );

	function done( error, stdout, stderr ) {
		var re;
		if ( error ) {
			t.fail( error.message );
		} else {
			re = stdout.toString();
			t.strictEqual( re, expected, 'expected value' );
			t.strictEqual( stderr.toString(), '', 'does not print to `stderr`' );

			re = re.substring( 0, re.length-1 );
			re = reFromString( re );

			// Test that the regular expression positively validates:
			t.strictEqual( re.test( fixture ), true, 'positively validates' );

			// Test that the regular expression negatively validates:
			t.strictEqual( re.test( 'beep boop bop' ), false, 'negatively validates' );
		}
		t.end();
	}
});

tape( 'if unable to generate a license header regular expression, the command-line interface prints a message to `stderr` and sets a non-zero exit code', opts, function test( t ) {
	var cmd = [
		EXEC_PATH,
		fpath,
		'--license=Apache-2.0',
		'--type=dafkadjlfjslfjsa'
	];

	exec( cmd.join( ' ' ), done );

	function done( error, stdout, stderr ) {
		if ( error ) {
			t.pass( error.message );
			t.strictEqual( error.code, 1, 'expected exit code' );
		}
		t.strictEqual( stdout.toString(), '', 'does not print to `stdout`' );
		t.strictEqual( stderr.toString().length > 0, true, 'prints a message to `stderr`' );
		t.end();
	}
});
