tape( 'the command-line interface requires a header argument, and, if not provided, the command-line interface prints an error and sets a non-zero exit code', opts, function test( t ) {
	var opts;
	var cmd;
	var f;

	// Create a temporary file:
	f = tmp( false );

	// Attempt to insert headers:
	cmd = [
		EXEC_PATH,
		'-e',
		'"process.stdin.isTTY = true; process.argv[ 2 ] = \''+f+'\'; require( \''+fpath+'\' );"'
	];
	opts = {
		'cwd': __dirname
	};

	exec( cmd.join( ' ' ), opts, done );

	function done( error, stdout, stderr ) {
		var expected;
		var actual;
		if ( error ) {
			t.pass( error.message );
			t.strictEqual( error.code, 1, 'expected exit code' );
		}
		t.strictEqual( stdout.toString(), '', 'does not print to `stdout`' );
		t.strictEqual( stderr.toString().length > 0, true, 'prints a message to `stderr`' );

		// Check that the temporary file has not changed:
		actual = readFileSync( f, 'utf8' );
		expected = readFileSync( FILE, 'utf8' );
		t.strictEqual( actual, expected, 'file has not changed' );

		// Cleanup:
		cleanup();
		t.strictEqual( existsSync( f ), false, 'tmp file removed' );

		t.end();
	}
});

