'use strict';

// MAIN //

/**
* Initializes a results object from a provided list of entry points.
*
* @private
* @param {ObjectArray} entries - list of entry points
* @returns {ObjectArray} results object
*/
function init( entries ) {
	var out;
	var o;
	var i;

	out = [];
	for ( i = 0; i < entries.length; i++ ) {
		o = entries[ i ];
		out.push({
			'pkg': o.pkg,
			'dir': o.dir,
			'aliases': {},
			'files': [],
			'deps': []
		});
	}
	return out;
}


// EXPORTS //

module.exports = init;
