'use strict';

// MODULES //

var path = require( 'path' );
var logger = require( 'debug' );
var readJSON = require( '@stdlib/fs/read-json' );
var isString = require( '@stdlib/assert/is-string' ).isPrimitive;
var isPlainObject = require( '@stdlib/assert/is-plain-object' );
var objectKeys = require( '@stdlib/utils/keys' );


// VARIABLES //

var debug = logger( 'browser-pkg-deps:async:resolve-aliases' );
var FOPTS = {
	'encoding': 'utf8'
};


// MAIN //

/**
* Resolves package dependency aliases.
*
* @private
* @param {ObjectArray} results - results (output) object
* @param {ObjectArray} entries - entry points
* @param {Callback} clbk - callback to invoke after resolving package dependency aliases
*/
function resolve( results, entries, clbk ) {
	var errFLG;
	var entry;
	var count;
	var len;
	var i;

	len = entries.length;
	count = 0;

	debug( 'Resolving %d package aliases...', len );
	for ( i = 0; i < len; i++ ) {
		entry = entries[ i ];
		debug( 'Resolving package: %s (%d of %d)...', entry.pkg, i+1, len );
		readJSON( path.join( entry.dir, 'package.json' ), FOPTS, createClbk( i ) );
	}

	/**
	* Returns a callback to be invoked upon reading package meta data.
	*
	* @private
	* @param {NonNegativeInteger} idx - index
	* @returns {Callback} callback
	*/
	function createClbk( idx ) {
		var aliases = results[ idx ].aliases;
		var entry = entries[ idx ];
		var k = idx + 1;
		return onData;

		/**
		* Callback invoked upon resolving package meta data.
		*
		* @private
		* @param {(Error|null)} error - error object
		* @param {Object} data - meta data
		* @returns {void}
		*/
		function onData( error, data ) {
			var keys;
			var key;
			var v;
			var i;
			if ( errFLG ) {
				return;
			}
			if ( error ) {
				debug( 'Encountered an error when resolving package meta data: %s (%d of %d). Error: %s', entry.pkg, k, len, error.message );
				errFLG = true;
				return clbk( error );
			}
			debug( 'Successfully resolved package meta data: %s (%d of %d).', entry.pkg, k, len );

			if ( isString( data.browser ) ) {
				v = path.resolve( entry.dir, data.browser );

				// Check for a valid `main` field...
				if ( isString( data.main ) ) {
					key = path.resolve( entry.dir, data.main );
					aliases[ key ] = v;
				} else {
					key = path.join( entry.dir, 'index.js' );
					aliases[ key ] = v;
				}
			} else if ( isPlainObject( data.browser ) ) {
				keys = objectKeys( data.browser );
				for ( i = 0; i < keys.length; i++ ) {
					key = keys[ i ];
					v = path.resolve( entry.dir, data.browser[ key ] );
					key = path.resolve( entry.dir, key );
					aliases[ key ] = v;
				}
			}
			debug( 'Successfully resolved package aliases: %s (%d of %d).', entry.pkg, k, len );

			count += 1;
			if ( count === len ) {
				debug( 'Resolved all package aliases.' );
				return clbk( null, results );
			}
		}
	}
}


// EXPORTS //

module.exports = resolve;
