'use strict';

/**
* Compute the absolute value in-place.
*
* @module @stdlib/math/special/inabs
*
* @example
* var Float64Array = require( '@stdlib/array/float64' );
* var inabs = require( '@stdlib/math/special/inabs' );
*
* var x = new Float64Array( [ 1.0, -1.0, 0.0 ] );
*
* var y = inabs( x );
* // returns <Float64Array>[ 1.0, 1.0, 0.0 ]
*
* var bool = ( y === x );
* // returns true
*
* @example
* var inabs = require( '@stdlib/math/special/inabs' );
*
* var x = [ 1.0, -1.0, 0.0 ];
*
* var y = inabs( x );
* // returns [ 1.0, 1.0, 0.0 ]
*
* var bool = ( y === x );
* // returns true
*
* @example
* var array = require( '@stdlib/ndarray/array' );
* var inabs = require( '@stdlib/math/special/inabs' );
*
* var x = array( [ [ 1.0, -2.0 ], [ -3.0, 4.0 ] ] );
* // returns <ndarray>
*
* var y = inabs( x );
* // returns <ndarray>
*
* var v = y.get( 0, 1 );
* // 2.0
*
* var bool = ( y === x );
* // returns true
*/

// MAIN //

var main = require( './main.js' );


// EXPORTS //

module.exports = main;
