'use strict';

// MODULES //

var tape = require( 'tape' );
var Float64Array = require( '@stdlib/array/float64' );
var Int32Array = require( '@stdlib/array/int32' );
var array = require( '@stdlib/ndarray/array' );
var ndarray = require( '@stdlib/ndarray/ctor' );
var shape2strides = require( '@stdlib/ndarray/base/shape2strides' );
var dispatch = require( './../lib' );


// FIXTURES //

var table = require( './fixtures/table.js' );


// VARIABLES //

var ctor = ndarray( 'float64', 3 );


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.strictEqual( typeof dispatch, 'function', 'main export is a function' );
	t.end();
});

tape( 'the function throws an error if provided an invalid table object', function test( t ) {
	var values;
	var i;

	values = [
		'5',
		5,
		NaN,
		true,
		false,
		null,
		void 0,
		[],
		{
			'array': null
		},
		{
			'ndarray': [ 'number' ]
		},
		{
			'array': {}
		},
		{
			'array': [ 'float64' ]
		},
		{
			'ndarray': 'float32'
		},
		{
			'ndarray': [ 'float64' ]
		},
		function noop() {}
	];

	for ( i = 0; i < values.length; i++ ) {
		t.throws( badValue( values[ i ] ), Error, 'throws an error when provided ' + values[ i ] );
	}
	t.end();

	function badValue( value ) {
		return function badValue() {
			dispatch( value );
		};
	}
});

tape( 'the function returns a unary function', function test( t ) {
	var fcn = dispatch( table );
	t.strictEqual( typeof fcn, 'function', 'returns expected value' );
	t.strictEqual( fcn.length, 1, 'has expected arity' );
	t.end();
});

tape( 'the function returns a function which throws an error if provided a value other than an array-like object or ndarray', function test( t ) {
	var values;
	var inabs;
	var i;

	inabs = dispatch( table );

	values = [
		'5',
		5,
		NaN,
		null,
		void 0,
		true,
		false,
		{},
		function noop() {}
	];

	for ( i = 0; i < values.length; i++ ) {
		t.throws( badValue( values[ i ] ), TypeError, 'throws an error when provided ' + values[ i ] );
	}
	t.end();

	function badValue( value ) {
		return function badValue() {
			inabs( value );
		};
	}
});

tape( 'the function returns a function which throws an error if provided an array-like object when a table resolution object is unable to resolve a corresponding function', function test( t ) {
	var inabs = dispatch( {} );
	t.throws( notSupported, TypeError, 'throws an error' );
	t.end();

	function notSupported() {
		inabs( [ 1.0, 2.0, 3.0 ] );
	}
});

tape( 'the function returns a function which throws an error if provided an ndarray when a table resolution object is unable to resolve a corresponding function', function test( t ) {
	var inabs = dispatch( {} );
	t.throws( notSupported, TypeError, 'throws an error' );
	t.end();

	function notSupported() {
		var x = array( [ 1.0, 2.0, 3.0 ] );
		inabs( x );
	}
});

tape( 'the function returns a function which supports operating on an array-like object in-place', function test( t ) {
	var expected;
	var inabs;
	var x;
	var y;

	inabs = dispatch( table );

	x = new Float64Array([
		-1.0,
		-3.14,
		2.0
	]);
	expected = new Float64Array([
		1.0,
		3.14,
		2.0
	]);

	y = inabs( x );
	t.strictEqual( y, x, 'returns expected value' );
	t.deepEqual( y, expected, 'returns expected value' );

	x = {
		'length': 3,
		'0': -1.0,
		'1': -3.14,
		'2': 2.0
	};
	expected = {
		'length': 3,
		'0': 1.0,
		'1': 3.14,
		'2': 2.0
	};

	y = inabs( x );
	t.strictEqual( y, x, 'returns expected value' );
	t.deepEqual( y, expected, 'returns expected value' );

	x = new ArrayLike([
		-1.0,
		-3.14,
		2.0
	]);
	expected = new ArrayLike([
		1.0,
		3.14,
		2.0
	]);

	y = inabs( x );
	t.strictEqual( y, x, 'returns expected value' );
	t.deepEqual( y, expected, 'returns expected value' );

	t.end();

	function ArrayLike( x ) {
		var i;
		if ( !(this instanceof ArrayLike) ) {
			return new ArrayLike( x );
		}
		for ( i = 0; i < x.length; i++ ) {
			this[ i ] = x[ i ];
		}
		this.length = x.length;
		return this;
	}
});

