'use strict';

// MAIN //

/**
* Resolves a function corresponding to a specified data type.
*
* @private
* @param {ArrayLikeObject} arr - array consisting of dtype-function pairs
* @param {string} dtype - data type for which to resolve a corresponding function
* @returns {(Function|null)} resolved function
*/
function resolve( arr, dtype ) {
	var i;
	for ( i = 0; i < arr.length; i += 2 ) {
		if ( arr[ i ] === dtype ) {
			return arr[ i+1 ];
		}
	}
	return null;
}


// EXPORTS //

module.exports = resolve;
