'use strict';

// MODULES //

var tape = require( 'tape' );
var proxyquire = require( 'proxyquire' );
var getKey = require( './../lib/key.js' );


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.strictEqual( typeof getKey, 'function', 'main export is a function' );
	t.end();
});

tape( 'the function returns a cache key (codegen=true)', function test( t ) {
	var opts;
	var key;

	opts = {
		'codegen': true
	};

	key = getKey( 'float64', opts );
	t.strictEqual( key, 'float64,codegen,true', 'returns expected value' );

	key = getKey( 'float32', opts );
	t.strictEqual( key, 'float32,codegen,true', 'returns expected value' );

	key = getKey( 'generic', opts );
	t.strictEqual( key, 'generic,codegen,true', 'returns expected value' );

	key = getKey( 'int16', opts );
	t.strictEqual( key, 'int16,codegen,true', 'returns expected value' );

	key = getKey( 'int32', opts );
	t.strictEqual( key, 'int32,codegen,true', 'returns expected value' );

	key = getKey( 'int8', opts );
	t.strictEqual( key, 'int8,codegen,true', 'returns expected value' );

	key = getKey( 'uint16', opts );
	t.strictEqual( key, 'uint16,codegen,true', 'returns expected value' );

	key = getKey( 'uint32', opts );
	t.strictEqual( key, 'uint32,codegen,true', 'returns expected value' );

	key = getKey( 'uint8', opts );
	t.strictEqual( key, 'uint8,codegen,true', 'returns expected value' );

	key = getKey( 'uint8c', opts );
	t.strictEqual( key, 'uint8c,codegen,true', 'returns expected value' );

	key = getKey( 'binary', opts );
	t.strictEqual( key, 'binary,codegen,true', 'returns expected value' );

	t.end();
});

