tape( 'the function returns a cache key (provides support for future options)', function test( t ) {
	var getKey;
	var opts;
	var key;

	getKey = proxyquire( './../lib/key.js', {
		'./options.json': [ 'codegen', 'beep', 'boop' ]
	});

	opts = {
		'codegen': true,
		'beep': 1,
		'boop': null
	};

	key = getKey( 'float64', opts );
	t.strictEqual( key, 'float64,codegen,true,beep,1,boop,null', 'returns expected value' );

	key = getKey( 'float32', opts );
	t.strictEqual( key, 'float32,codegen,true,beep,1,boop,null', 'returns expected value' );

	key = getKey( 'generic', opts );
	t.strictEqual( key, 'generic,codegen,true,beep,1,boop,null', 'returns expected value' );

	key = getKey( 'int16', opts );
	t.strictEqual( key, 'int16,codegen,true,beep,1,boop,null', 'returns expected value' );

	key = getKey( 'int32', opts );
	t.strictEqual( key, 'int32,codegen,true,beep,1,boop,null', 'returns expected value' );

	key = getKey( 'int8', opts );
	t.strictEqual( key, 'int8,codegen,true,beep,1,boop,null', 'returns expected value' );

	key = getKey( 'uint16', opts );
	t.strictEqual( key, 'uint16,codegen,true,beep,1,boop,null', 'returns expected value' );

	key = getKey( 'uint32', opts );
	t.strictEqual( key, 'uint32,codegen,true,beep,1,boop,null', 'returns expected value' );

	key = getKey( 'uint8', opts );
	t.strictEqual( key, 'uint8,codegen,true,beep,1,boop,null', 'returns expected value' );

	key = getKey( 'uint8c', opts );
	t.strictEqual( key, 'uint8c,codegen,true,beep,1,boop,null', 'returns expected value' );

	key = getKey( 'binary', opts );
	t.strictEqual( key, 'binary,codegen,true,beep,1,boop,null', 'returns expected value' );

	t.end();
});
